package com.learning.kotlinyoutubeimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport androidx.recyclerview.widget.LinearLayoutManagerimport com.google.gson.GsonBuilderimport kotlinx.android.synthetic.main.activity_main.*import okhttp3.*import java.io.IOExceptionclass MainActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        recyclerview_main.layoutManager = LinearLayoutManager(this)        getData()    }    fun getData() {        val url = "https://api.letsbuildthatapp.com/youtube/home_feed"        val request = Request.Builder().url(url).build()        val client = OkHttpClient()        client.newCall(request = request).enqueue(object : Callback {            override fun onResponse(call: Call, response: Response) {                val body = response.body?.string()                val gson = GsonBuilder().create()                val homeFeed = gson.fromJson(body, HomeFeed::class.java)                runOnUiThread {                    recyclerview_main.adapter = MainAdapter(homeFeed = homeFeed)                }            }            override fun onFailure(call: Call, e: IOException) {                println("Fail with $e")            }        })    }}class HomeFeed(val videos: List<Video>)class Video(    val id: Int,    val name: String,    val link: String,    val imageUrl: String,    val numberOfViews: Int,    val channel: Channel)class Channel(val name: String, val profileImageUrl: String)